        include_once("tpl/actions/conexao.php");
        $sql_nc = '
            SELECT DAY(NOW()) dia_atual, MONTH(NOW()) mes_atual, YEAR(NOW()) ano_atual, livro_registo.num_contrato, 
            angariacoes.tipo_negocio, livro_registo.nome_proprietario, livro_registo.arruamento_proprietario, 
            livro_registo.num_porta_proprietario, livro_registo.complemento_proprietario, livro_registo.cod_postal_proprietario, 
            (SELECT lista_cep_localidade.localidade FROM lista_cep_localidade WHERE lista_cep_localidade.cod_postal = livro_registo.cod_postal_proprietario) localidade_proprietario, 
            colaboradores.nome nome_consultor, info_loja.nome nome_loja, info_loja.concelho localidade_loja, info_loja.telefone telefone_loja, 
            info_loja.email email_loja, info_loja.rua_ave arruamento_loja, info_loja.num_porta num_porta_loja, 
            info_loja.complemento complemento_loja, info_loja.freguesia freguesia_loja, info_loja.cod_postal cod_postal_loja
            FROM livro_registo
            INNER JOIN angariacoes ON angariacoes.id = livro_registo.angariacao_id
            INNER JOIN id_angariacao ON id_angariacao.angariacao_id = livro_registo.angariacao_id
            INNER JOIN colaboradores ON colaboradores.id = id_angariacao.consultor
            INNER JOIN info_loja ON info_loja.id = id_angariacao.loja
            WHERE 
             SUBSTRING_INDEX(num_contrato, "/", 1) = "'.$store_code.'" AND 
             SUBSTRING_INDEX(SUBSTRING_INDEX(num_contrato, "/", -2), "/", 1) BETWEEN '.$nc_from_int.' AND '.$nc_to_int.' AND 
             SUBSTRING_INDEX(num_contrato, "/", -1) BETWEEN '.$nc_from_year.' AND '.$nc_to_year.'
            ORDER BY CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(num_contrato, "/", -2), "/", 1) AS UNSIGNED), SUBSTRING_INDEX(num_contrato, "/", -1)
        ';
        $result_sql_nc = mysqli_query($conn, $sql_nc);
        if (mysqli_num_rows($result_sql_nc) > 0) {
